//
// Simple Carousel (http://christianheilmann.com/2015/04/08/keeping-it-simple-coding-a-carousel/)
//
carousel = (function(){
  if( document.querySelector('.story_carousel') ) {
      var box = document.querySelector('.story_carousel');
      var next = box.querySelector('.story_carousel__forward_btn');
      var prev = box.querySelector('.story_carousel__back_btn');
      var items = box.querySelectorAll('.story_carousel__story');
      //var item_width = $(items[0]).width();
      var counter = 0;
      var amount = items.length;
      var current = items[0];

      box.classList.add('js-active');

      function navigate(direction) {
        var previous_counter = counter;

        current.classList.remove('js-current');

        counter = counter + direction;
        if (direction === -1 && counter < 0) { 
          counter = amount - 1; 
        }
        if (direction === 1 && !items[counter]) { 
          counter = 0;
        }
        current = items[counter];
        current.classList.add('js-current');

        if( previous_counter !== counter ) {
            box.classList.remove('js-current-'+previous_counter);
            box.classList.add('js-current-'+counter);
        }

      }

      next.addEventListener('click', function(ev) {
        navigate(1);
      });

      prev.addEventListener('click', function(ev) {
        navigate(-1);
      });
      navigate(0);
  }
})();

